import React from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { useForm } from 'react-hook-form';
import UsernameInput from './usernameInput';

function LoginForm() {

    const { register, handleSubmit, errors } = useForm();

    const onSubmit = data => {
        console.log('submit', data);
    }; // your form submit function which will invoke after successful validation

  return (

        <div>
            <h1>To subscribe to this website, please enter your email address here. We will send updates
                            occasionally.</h1>
            <form onSubmit={handleSubmit(onSubmit)} noValidate>

            {/* <TextField
                    label="User Name"
                    name="username"
                    variant="outlined"
                    error={!!errors.username}
                    inputRef={register({required: true})}
                    helperText={errors.username && '*Required!'}
                /> */}
                <UsernameInput register="register"/>

                <TextField
                    label="Email address"
                    name="email"
                    autoComplete="email"
                    variant="outlined"
                    error={!!errors.email}
                    inputRef={register({
                        required: true,
                        pattern: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
                    })}
                    helperText={errors.email && 'Invalid email address!'}
                />

                <TextField
                    label="Password"
                    name="password"
                    variant="outlined"
                    error={!!errors.password}
                    inputRef={register({
                        required: true,
                        pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?!.*\s).*$/
                    })}
                    helperText={errors.password && 'Password should contain UpperCase, LowerCase and Number!'}
                />

                <Button type="submit">submit</Button>
            </form>
        </div>
  );
}

export default LoginForm;
