import React, { Fragment } from 'react';
import { useParams } from 'react-router-dom';
import { Avatar } from '@material-ui/core';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Typography from '@material-ui/core/Typography';
import ArrowForwardIosRoundedIcon from '@material-ui/icons/ArrowForwardIosRounded';

function CandidateProfile() {
  const candidate = JSON.parse(decodeURIComponent(useParams()));
  return (
    <div className="container">
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar src={candidate.avatar} />
        </ListItemAvatar>
        <ListItemText
          primary={candidate.first_name + ' ' + candidate.last_name}
          secondary={
            <Fragment>
              <Typography component="span" variant="body2" color="textPrimary">
                {candidate.job_title}
              </Typography>
            </Fragment>
          }
        />
        <Button href={`/candidates/${encodeURIComponent(JSON.stringify(candidate))}`} color="primary"
          endIcon={<ArrowForwardIosRoundedIcon />}
        >Full Details
            </Button>
      </ListItem>
    </div>

  );
}

export default CandidateProfile;
