import Api from '../api';

const AuthHttp = {
    authUrl: '/auth',

    signup: (data) => {
        return Api.post(`${this.authUrl}/signup`, data);
    },

    signin: (data) => {
        return Api.post(`${this.authUrl}/signup`, data);
    },

    setToken: (token) => {
        Api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
}

export default AuthHttp;
