import React, { Fragment, useEffect, useState } from 'react';

import { Button } from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import ArrowForwardIosRoundedIcon from '@material-ui/icons/ArrowForwardIosRounded';

import './candidatesList.scss'
import CandidateHttp from './candidateHttp';

function CandidatesList(props) {
  const [data, setData] = useState([]);

  useEffect(() => {
    CandidateHttp.all().then(candidatesData => {
      setData(candidatesData.data.candidates);
    });
  }, []);

  const viewProfile = (candidate) => {
    // props.history.push(`/${candidate.id}`, {candidate});
  }

  return (
    <List className="candidates-list">
      {data.map((candidate, i) => {
        return <Fragment key={i}>
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Avatar alt="Remy Sharp" src={candidate.avatar} />
            </ListItemAvatar>
            <ListItemText
              primary={candidate.first_name + ' ' + candidate.last_name}
              secondary={
                <Fragment>
                  <Typography component="span" variant="body2" color="textPrimary">
                    {candidate.job_title}
                  </Typography>
                </Fragment>
              }
            />
            <Button href={`/candidates/${candidate.id}?data=`} color="primary"
              endIcon={<ArrowForwardIosRoundedIcon />}
            >Full Details
            </Button>
            {/* <Button onClick={viewProfile} color="primary"
              endIcon={<ArrowForwardIosRoundedIcon />}
            >Full Details
            </Button> */}
          </ListItem>
          { i < data.length-1 && <Divider variant="inset" component="li" /> }
          
        </Fragment>
      })}

    </List>
  );
}

export default CandidatesList;
