import Api from '../api';

const authUrl = '/auth';
const AuthHttp = {

    signup: (data) => {
        return Api.post(`${authUrl}/signup`, data);
    },

    signin: (data) => {
        return Api.post(`${authUrl}/signin`, data);
    },

    setToken: (token) => {
        // Api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        localStorage.setItem('token', token);
    }
}

export default AuthHttp;
